mapper.update:
update board_tb
set board_cnt = board_cnt +1
where board_no = #{board_no}


UPDATE board_tb
SET delete_yn = 'Y'
where board_no = #{board_no}

UPDATE board_reply
SET reply_delete_yn = 'Y'
where reply_no = #{reply_no}

mapper.select:
select COUNT(*) from board_reply 
where reply_delete_yn = 'N'
and board_no = #{board_no}
