mapper.select:
SELECT mth.*, tm.team_mark, tm.team_win, tm.team_name, tm.team_etire, tm.team_lose, tm.team_tie, tm.team_cnt, tm.team_region FROM match mth inner join team_tb tm on mth.blueteam_no = tm.team_no where 1=1 AND mth.match_sport = #{team_sport}
and match_region=#{selectRegion}
AND to_char(fight_date, 'yyyy/mm') = trim(to_char(#{year }, '0000')) || '/' || trim(to_char(#{month }, '00')) order by fight_date

mapper.update:
UPDATE match
SET purpleteam_no=#{user.team_no}
WHERE match_no=#{mno}
